import {useState, useEffect} from 'react'
import {Api} from './api'
import './styles/global.scss'
import {Bar} from './components/Bar'
import home from './images/home.svg'
import livro from './images/Livro.svg'
import profile from './images/profile.svg'
import {Container} from './components/container'
import {CardComp} from './components/CardComp'

function App() {
  const [books, setbooks] = useState([]);
  const [input, setinput] = useState('');

  async function HandleChange (e){
  setinput(e.target.value);


  try{
    const result = await Api.api(input)
    const res = result.data.items;
    setbooks(res)
  }catch(err){
    console.log(err)
  }

 }



  return (
  <div className="App">
    <div className="Input">
      <button></button>
      <input onChange={e => HandleChange(e)} placeholder="Search Book" />
    </div>
    {!input && <div className="content-no-search">
      <div className="hi-message-container">
      <h3>Hi,<h3 className="separator">Name</h3><div className="emoji"></div></h3>
    </div>
    <Container></Container>
    <div className="current">
      <div className="title-bnt"><h3></h3></div>
    </div>
    <Bar>
      <div className="opt"> <img src={home}/> </div>
      <div className="opt"> <img src={livro}/></div>
      <div className="opt"> <img src={profile}/></div>
    </Bar>
    </div> }

    {input && <>

      <div className="collection">
      {books.map((value)=>(


      <CardComp back={value.volumeInfo.imageLinks.thumbnail} litle={value.volumeInfo.imageLinks.thumbnail} title={value.volumeInfo.title} ></CardComp>


    ))}
       </div>
    </>}


  </div>
  );
}

export default App;
