}




  return (
  <div className="App">
    <div className="Input">
      <button></button>
      <input value={input} onChange={e => HandleChange(e)} placeholder="Search Book" />
    </div>
    {!input && <div className="content-no-search">
      <div className="hi-message-container">
      <h3>Hi,<h3 className="separator">Name</h3><div className="emoji"></div></h3>
    </div>
    <Container></Container>
    <div className="current">
      <div className="title-bnt"><h3></h3></div>
    </div>

    </div> }

    {input && <>


      <div className="collection">
      {books.map((value)=>(


        <CardComp key={value.volumeInfo.title} url={value.volumeInfo.imageLinks === undefined ? '' : `${value.volumeInfo.imageLinks.thumbnail}`} back={value.volumeInfo.imageLinks === undefined ? '' : `${value.volumeInfo.imageLinks.thumbnail}`}  title={value.volumeInfo.title} ></CardComp>


    ))}
       </div>

    </>}
      {!input && <Bar>
      <div className="opt"> <img src={home}/> </div>
      <div className="opt"> <img src={livro}/></div>
      <div className="opt"> <img src={profile}/></div>
    </Bar> }

    {input && books ? <>
    <button onClick={(e) => setmin(min + 9)} className="btn">cARREGAR</button>
    </> : ''}
  </div>
  );
}

export default App;
